                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                DROP TABLE IF EXISTS store_product;
DROP TABLE IF EXISTS product;
DROP TABLE IF EXISTS category;
DROP TABLE IF EXISTS store;
CREATE TABLE category (id INT UNSIGNED PRIMARY KEY AUTO_INCREMENT, name VARCHAR(200) NOT NULL);
CREATE TABLE store (id INT UNSIGNED PRIMARY KEY AUTO_INCREMENT, name VARCHAR(100) UNIQUE NOT NULL);
CREATE TABLE store_product (id INT UNSIGNED PRIMARY KEY AUTO_INCREMENT, store_id INT UNSIGNED REFERENCES store(id), product_code BIGINT UNSIGNED REFERENCES product(code));
CREATE TABLE product (code BIGINT UNSIGNED PRIMARY KEY, name VARCHAR(200) NOT NULL, brand VARCHAR(200) NOT NULL, url VARCHAR(255) NOT NULL, nutriscore CHAR(1) NOT NULL, category_id TINYINT UNSIGNED REFERENCES category(id));
CREATE TABLE IF NOT EXISTS client (id INT UNSIGNED PRIMARY KEY AUTO_INCREMENT, email VARCHAR(100) UNIQUE NOT NULL, password VARCHAR(255) NOT NULL);
CREATE TABLE IF NOT EXISTS favorite (client_id INT UNSIGNED REFERENCES client(id), product_id BIGINT UNSIGNED REFERENCES product(code));